import './App.css';
import React,{useState,useEffect} from 'react';
function App() {
  const [currentTime,setCurrentTime]= useState(0);
  useEffect(()=>{
    fetch('/time').then(res=>res.json()).then(data=>{
        setCurrentTime(data.time)
    });
  });
  return (
    <div className="App">
      <header className="App-header">
        <p>
          time :{currentTime}
        </p>

      </header>
    </div>
  );
}

export default App;
